settings.custom_liquid:
{% if product.metafields.custom.product_set.value != blank %}
<div class="recommended-container">
  <div class="recommended-section">
    <h2 class="section-title">Recommended Products</h2>
    <div class="swiper product-swiper">
      <div class="swiper-wrapper">
        {% for item_product in product.metafields.custom.product_set.value %}
          {% if item_product and item_product.variants and item_product.variants.first %}
            <div class="swiper-slide product-card">
              <img src="{{ item_product.featured_image | image_url: width: 200 }}" alt="{{ item_product.title }}" class="product-image" />
              <div class="product-info">
                <div class="product-title">{{ item_product.title }}</div>
                <div class="product-price">{{ item_product.price | money }}</div>
              </div>
              <form method="post" action="{{ routes.cart_add_url }}" class="add-recommended-product-form">
                <input type="hidden" name="id" value="{{ item_product.variants.first.id }}">
                <button type="submit" class="add-to-cart">
                    +
                </button>
              </form>
            </div>
          {% endif %}
        {% endfor %}
      </div>
      <div class="swiper-pagination"></div>
    </div>
  </div>
</div>
<style>
  @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap');

  .recommended-container {
    border: 1px solid #eee;
    border-radius: 10px;
    background-color: #fff;
    padding: 1px 15px;
    margin: 1px auto;
    max-width: 100%;
    box-sizing: border-box;
  }

  .recommended-section {
    width: 100%;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Poppins', sans-serif;
  }

  .section-title {
    font-size: 18px;
    font-weight: bold;
    margin-bottom: 12px;
    padding-left: calc(5px + 12px);
  }

  .swiper.product-swiper {
    width: 100%;
    overflow: hidden;
    padding: 15px 5px;
    box-sizing: border-box;
  }

  .swiper-slide.product-card {
    display: flex;
    align-items: center;
    background: #fff;
    border-radius: 16px;
    padding: 12px;
    gap: 12px;
    width: 100%;
    box-sizing: border-box;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
  }

  .product-image {
    width: 64px;
    height: 64px;
    object-fit: cover;
    border-radius: 8px;
  }

  .product-info {
    flex: 1;
    overflow: hidden;
  }

  .product-title {
    font-size: 14px;
    font-weight: 600;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  .product-price {
    font-size: 13px;
    color: #333;
    margin-top: 4px;
  }

  .add-to-cart {
    width: 36px;
    height: 36px;
    background: #000;
    color: #fff;
    border: none;
    border-radius: 50%;
    font-size: 20px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
    transition: background-color 0.3s ease;
    position: relative;
    overflow: hidden; /* Ensure overflow is hidden */
    text-align: center; /* Center text if still visible */
  }

  .add-to-cart:hover {
      background-color: #333;
  }

  .add-to-cart.loading {
      color: transparent; /* Hide the "+" text */
      pointer-events: none; /* Prevent multiple clicks */
      background-color: #999; /* Optional: change background when loading */
      position: relative !important; /* Add !important just in case theme overrides */
      overflow: hidden !important; /* Add !important just in case theme overrides */
  }

  .add-to-cart.loading::after {
      content: '';
      position: absolute;
      top: 50%;
      left: 50%;
      width: 20px;
      height: 20px;
      border: 3px solid #fff;
      border-top-color: transparent;
      border-radius: 50%;
      animation: spin 0.8s linear infinite;
      /* Use transform for centering, overrides margin if present */
      transform: translate(-50%, -50%);
  }

  @keyframes spin {
      0% { transform: translate(-50%, -50%) rotate(0deg); }
      100% { transform: translate(-50%, -50%) rotate(360deg); }
  }


  .swiper-button-prev,
  .swiper-button-next {
    color: #000;
  }

  .swiper-pagination {
      position: relative;
      bottom: auto !important;
      top: 1px;
    width: 100%;
  }

  .swiper-pagination-bullet {
      background: #999;
      opacity: 0.8;
      margin: 0 4px;
      width: 8px;
      height: 8px;
      display: inline-block;
      border-radius: 50%;
      cursor: pointer;
  }
@media screen and (max-width: 767.98px) {
      div.recommended-container div.swiper-pagination span.swiper-pagination-bullet {
          width: 6px !important;
          height: 6px !important;
          margin: 0 3px !important;
          padding: 0 !important; /* This overrides the problematic padding */
      }
       div.recommended-container div.swiper-pagination span.swiper-pagination-bullet-active {
          width: 6px !important;
          height: 6px !important;
          padding: 0 !important; /* Override padding for active state too */
       }
  }

  .swiper-pagination-bullet-active {
      background: #000;
      opacity: 1;
  }
</style>
<script src="https://cdn.jsdelivr.net/npm/swiper@9/swiper-bundle.min.js"></script>
<script>
  document.addEventListener('DOMContentLoaded', function() {
    if (document.querySelector('.product-swiper')) {
      const productSwiper = new Swiper('.product-swiper', {
        slidesPerView: 1,
        spaceBetween: 16,
        grabCursor: true,
        loop: false,
        pagination: {
          el: '.swiper-pagination',
          clickable: true,
        },
        on: {
            init: function () {
                if (this.slides.length <= 1) {
                    this.pagination.$el.hide();
                }
            },
            update: function () {
                 if (this.slides.length <= 1) {
                    this.pagination.$el.hide();
                } else {
                     this.pagination.$el.show();
                }
            }
        }
      });
    }

    const recommendedAddForms = document.querySelectorAll('.recommended-container .add-recommended-product-form');

    recommendedAddForms.forEach(form => {
      const button = form.querySelector('.add-to-cart');

      form.addEventListener('submit', async function(event) {
        event.preventDefault();

        const variantId = form.querySelector('input[name="id"]').value;
        const quantityInput = form.querySelector('input[name="quantity"]');
        const quantity = quantityInput ? parseInt(quantityInput.value) : 1;

        if (!variantId) {
          console.error('Variant ID not found for add to cart form.');
          return;
        }

        const cartElement = document.querySelector("cart-notification") || document.querySelector("cart-drawer");
        const formData = new FormData();
        formData.append('id', variantId);
        formData.append('quantity', quantity);

        if (cartElement && typeof cartElement.getSectionsToRender === 'function') {
             cartElement.getSectionsToRender().forEach(section => {
                 formData.append('sections[]', section.id);
             });
             formData.append('sections_url', window.location.pathname);
        }

        button.classList.add('loading');


        try {
            const addItemResponse = await fetch(`${routes.cart_add_url}.js`, {
                method: 'POST',
                headers: {
                   'X-Requested-With': 'XMLHttpRequest'
                },
                body: formData
            });

            if (!addItemResponse.ok) {
                 const errorData = await addItemResponse.json().catch(() => ({ description: 'Unknown error' }));
                 throw new Error(errorData.description || 'Could not add item to cart.');
            }

            const parsedState = await addItemResponse.json();

            console.log('Add to cart request successful.');

            let cartData = null;
            try {
                 const cartResponse = await fetch('/cart.json');
                 if (cartResponse.ok) {
                     cartData = await cartResponse.json();
                     console.log('Latest cart data fetched:', cartData);
                 } else {
                     console.error('Failed to fetch /cart.json after add.');
                 }
            } catch (fetchCartError) {
                 console.error('Error fetching cart.json:', fetchCartError);
            }


            if (parsedState.sections) {
                 if (cartElement && typeof cartElement.getSectionsToRender === 'function') {
                     cartElement.getSectionsToRender().forEach(section => {
                         const elementToReplace = document.getElementById(section.id);
                         if (elementToReplace && parsedState.sections[section.id]) {
                             try {
                                 const html = new DOMParser().parseFromString(parsedState.sections[section.id], "text/html");
                                 const minicartFormContent = html.querySelector("#minicart-form");
                                 if(minicartFormContent) {
                                    elementToReplace.innerHTML = minicartFormContent.innerHTML;

                                    if (typeof BlsAnimations !== 'undefined' && typeof BlsAnimations.innit === 'function') BlsAnimations.innit();
                                    if (typeof BlsLazyloadImg !== 'undefined' && typeof BlsLazyloadImg.init === 'function') BlsLazyloadImg.init();
                                    if (typeof initLazyloadItem === 'function') initLazyloadItem();
                                     if (cartElement && typeof cartElement.cartAction === 'function') {
                                        cartElement.cartAction();
                                     }

                                 } else {
                                    console.warn('Could not find #minicart-form inside the fetched section HTML.');
                                 }
                             } catch (parseError) {
                                 console.error('Error parsing section HTML:', parseError);
                             }

                         } else {
                            console.warn(`Could not find element with ID ${section.id} or section data in add response.`);
                         }
                     });
                 } else {
                    console.warn('Cart element or getSectionsToRender method not available for sections update.');
                 }
            } else {
                 console.warn('Add to cart response did not contain sections data.');
            }

            if (cartData && cartData.item_count !== undefined) {
               document.querySelectorAll(".cart-count").forEach(el => {
                 if (el.classList.contains("cart-count-drawer")) {
                   el.innerHTML = `(${cartData.item_count})`;
                 } else {
                   el.innerHTML = cartData.item_count > 100 ? '~' : cartData.item_count;
                 }
               });
               if (document.querySelector("header-total-price")) {
                  const headerTotalPrice = document.querySelector("header-total-price");
                  if (typeof headerTotalPrice.updateTotal === 'function') {
                      headerTotalPrice.updateTotal(cartData);
                  } else {
                      console.warn('header-total-price element found, but no updateTotal method.');
                  }
               }
               const cartFreeShip = document.querySelector("free-ship-progress-bar");
               if (cartFreeShip && typeof cartFreeShip.init === 'function') {
                   cartFreeShip.init(cartData.items_subtotal_price);
               }
            } else {
                 console.warn('Cart data not available from /cart.json fetch.');
            }

            if (cartElement) {
                if (typeof cartElement.open === 'function') {
                  cartElement.open();
                } else {
                   console.warn('Cart element found, but open() method not available.');
                }

                 document.dispatchEvent(new CustomEvent('cart:add', { detail: { item: parsedState.items ? parsedState.items[0] : parsedState, cart: cartData } }));
                 document.dispatchEvent(new CustomEvent('cart:updated', { detail: { cart: cartData } }));

            } else {
               console.warn('Cart notification or drawer element not found. Cannot open or dispatch.');
            }

        } catch (error) {
            console.error('Error adding product to cart:', error);
            alert('Error adding product to cart: ' + error.message);
        } finally {
            button.classList.remove('loading');
        }
      });
    });
  });
</script>
{% endif %}
