settings.custom_liquid:
{% if product.metafields.custom.product_set.value != blank %}
<div class="recommended-products">
  <div class="recommended-products__section">
    <h2 class="recommended-products__title">Recommended Products</h2>
    <div class="swiper recommended-products__swiper">
      <div class="swiper-wrapper">
        {% for item_product in product.metafields.custom.product_set.value %}
          {% if item_product and item_product.variants and item_product.variants.first %}
            <div class="swiper-slide recommended-products__card">
              <img src="{{ item_product.featured_image | image_url: width: 200 }}" alt="{{ item_product.title }}" class="recommended-products__image" />
              <div class="recommended-products__info">
                <div class="recommended-products__product-title">{{ item_product.title }}</div>
                <div class="recommended-products__price">{{ item_product.price | money }}</div>
              </div>
              <form method="post" action="/cart/add" class="recommended-products__form">
                <input type="hidden" name="id" value="{{ item_product.variants.first.id }}">
                <button type="submit" class="recommended-products__add-button">+</button>
              </form>
            </div>
          {% endif %}
        {% endfor %}
      </div>
      <div class="swiper-pagination"></div>
    </div>
  </div>
</div>
<style>
  .recommended-products { border: 1px solid #eee; border-radius: 10px; background-color: #fff; padding: 1px 15px; margin: 1px auto; max-width: 100%; box-sizing: border-box; font-family: 'Poppins', sans-serif; }
  .recommended-products__title { font-size: 18px; font-weight: bold; margin-bottom: 12px; padding-left: 17px; }
  .recommended-products__swiper { width: 100%; overflow: hidden; padding: 15px 5px; box-sizing: border-box; }
  .recommended-products__card { display: flex; align-items: center; background: #fff; border-radius: 16px; padding: 12px; gap: 12px; width: 100%; box-sizing: border-box; box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15); }
  .recommended-products__image { width: 64px; height: 64px; object-fit: cover; border-radius: 8px; }
  .recommended-products__info { flex: 1; overflow: hidden; }
  .recommended-products__product-title { font-size: 14px; font-weight: 600; white-space: nowrap; overflow: hidden; text-overflow: ellipsis; }
  .recommended-products__price { font-size: 13px; color: #333; margin-top: 4px; }
  .recommended-products__add-button { width: 36px; height: 36px; background: #000; color: #fff; border: none; border-radius: 50%; font-size: 20px; cursor: pointer; display: flex; align-items: center; justify-content: center; flex-shrink: 0; transition: background-color 0.3s ease; position: relative; overflow: hidden; text-align: center; }
  .recommended-products__add-button:hover { background-color: #333; }
  .recommended-products__add-button.loading { color: transparent; pointer-events: none; background-color: #999; }
  .recommended-products__add-button.loading::after { content: ''; position: absolute; top: 50%; left: 50%; width: 20px; height: 20px; border: 3px solid #fff; border-top-color: transparent; border-radius: 50%; animation: spin 0.8s linear infinite; transform: translate(-50%, -50%); }
  @keyframes spin { 0% { transform: translate(-50%, -50%) rotate(0deg); } 100% { transform: translate(-50%, -50%) rotate(360deg); } }
  .swiper-pagination { position: relative; top: 1px; width: 100%; }
  .swiper-pagination-bullet { background: #999; opacity: 0.8; margin: 0 4px; width: 8px; height: 8px; border-radius: 50%; cursor: pointer; }
  .swiper-pagination-bullet-active { background: #000; opacity: 1; }
  @media screen and (max-width: 767.98px) { .swiper-pagination-bullet, .swiper-pagination-bullet-active { width: 6px !important; height: 6px !important; margin: 0 3px !important; padding: 0 !important; } }
</style>
<script src="https://cdn.jsdelivr.net/npm/swiper@9/swiper-bundle.min.js"></script>
<script>
  document.addEventListener('DOMContentLoaded', function() {
    const swiper = new Swiper('.recommended-products__swiper', {
      slidesPerView: 1, spaceBetween: 16, loop: true, grabCursor: true,
      autoplay: { delay: 3500, disableOnInteraction: false },
      pagination: { el: '.swiper-pagination', clickable: true }
    });

    const forms = document.querySelectorAll('.recommended-products__form');
    forms.forEach(form => {
      form.addEventListener('submit', async function(e) {
        e.preventDefault();
        const button = form.querySelector('.recommended-products__add-button');
        button.classList.add('loading');

        const variantId = form.querySelector('input[name="id"]').value;
        let cartData;

        try {
          const addToCartResponse = await fetch('/cart/add.js', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ items: [{ id: variantId, quantity: 1 }] })
          });
          if (!addToCartResponse.ok) throw new Error('Failed to add item to cart.');
          cartData = await addToCartResponse.json();

          const sectionResponse = await fetch('/?section_id=minicart-form');
          const newCartHTMLText = await sectionResponse.text();

          const minicartFormElement = document.getElementById('minicart-form');
          const newContent = new DOMParser().parseFromString(newCartHTMLText, 'text/html').getElementById('minicart-form');
          if (minicartFormElement && newContent) {
            minicartFormElement.innerHTML = newContent.innerHTML;
          }

          document.dispatchEvent(new CustomEvent('cart:updated', {
            bubbles: true,
            detail: { cart: cartData }
          }));

          const cartDrawer = document.getElementById('minicart_wrapper');
          if (cartDrawer) {
            if (typeof cartDrawer.open === 'function') {
                cartDrawer.open();
            } else {
                cartDrawer.classList.add('open');
            }
          }

        } catch (err) {
          console.error('Error during cart operation:', err);
          alert('Error: Could not add product to cart.');
        } finally {
          button.classList.remove('loading');
        }
      });
    });
  });
</script>
{% endif %}
